(53, 'information', 'admissions', '2', '1', '<div class="tileHeadingText">\r\n<h1> Come Check Us Out </h1>\r\n<h4>We&#39;d love to show you around</h4>\r\n\r\n<div class="textBlockContainer">\r\n<p> Still not convinced Forest City Nursing & Rehab Center is the right place for you? Schedule a tour and us <i>show</i> you firsthand what we have to offer! Take some time to come and meet some of the wonderful people here at our facility. Simply fill out the form located on this page and it will be delivered to one of our social workers.</p>\r\n\r\n<p>Or give Mary Ann Rogan a call today at (570) 785-3005.</p>\r\n</div>\r\n\r\n</div>', '<form class="form-horizontal well" id="nhTourForm">\r\n<fieldset>\r\n<input name="submitForm" value="true" type="hidden">\r\n<input name="formName" value="scheduleNHTour" type="hidden">\r\n<!-- Form Name -->\r\n<legend>Schedule A Tour</legend>\r\n\r\n<!-- Text input-->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="name">Name</label>  \r\n  <div class="col-md-9">\r\n  <input id="name" name="name" type="text" placeholder="First Last" class="form-control input-md" required="">\r\n    \r\n  </div>\r\n</div>\r\n\r\n<!-- Text input-->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="phone">Phone</label>  \r\n  <div class="col-md-9">\r\n  <input id="phone" name="phone" type="text" placeholder="(570) 555-5555" class="form-control input-md">\r\n    \r\n  </div>\r\n</div>\r\n\r\n<!-- Text input-->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="email">Email</label>  \r\n  <div class="col-md-9">\r\n  <input id="email" name="email" type="text" placeholder="example@domain.com" class="form-control input-md">\r\n    \r\n  </div>\r\n</div>\r\n\r\n<!-- Select Basic -->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="ServicesType">Service</label>\r\n  <div class="col-md-9">\r\n    <select id="serviceType" name="serviceType" class="form-control">\r\n      <option value="Long-Term Care">Long-Term Care</option>\r\n      <option value="Rehabilitation">Rehabilitation</option>\r\n      <option value="Hospice">Hospice</option>\r\n      <option value="Personal Care">Personal Care</option>\r\n    </select>\r\n  </div>\r\n</div>\r\n\r\n<!-- Appended Input-->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="desiredDate">Desired Date</label>\r\n  <div class="col-md-9">\r\n    <div class="input-group">\r\n      <input id="desiredDate" name="desiredDate" class="form-control" placeholder="mm/dd/yyyy" type="text">\r\n      <span class="input-group-addon"><span class="glyphicon glyphicon-calendar"></span> </span>\r\n    </div>\r\n    \r\n  </div>\r\n</div>\r\n\r\n<!-- Textarea -->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="comments">Additional Comments</label>\r\n  <div class="col-md-9">                     \r\n    <textarea class="form-control" id="comments" name="comments"></textarea>\r\n  </div>\r\n</div>\r\n\r\n<!-- Button -->\r\n<div class="form-group">\r\n  <label class="col-md-3 control-label" for="submit"></label>\r\n  <div class="col-md-9">\r\n    <button id="submitNHTour" name="submit" class="btn btn-maroon">Request A Tour</button>\r\n  </div>\r\n</div>\r\n\r\n</fieldset>\r\n</form>\r\n', '', '', '', '', '', '', '', '', '', '', NULL),
(55, 'contact', 'staff', '10', '4', '<div class=" employeeImgContainer">\r\n		<img class="img-circle center-block" src="/resources/images/dept/ashleyC.jpg"/>\r\n</div>\r\n<div class=""></div>', '<div class="employeeDescription center-block">\r\n\r\n<h4>Marketing Director</h4>\r\n<h1>Ashley Conrad</h1>\r\n\r\n<p>Ashley, our marketing director, has been part of our team for two years. Prior to to her current position, Ashley worked in our physical therapy department as a rehab technician. In her current position Ashley contacts physicians and numerous health care providers. She is involved in several community projects and long term care organizations. She resides with her husband Fred and her dogs, Gracie, Penny, Desi.</p>\r\n\r\n\r\n</div>', '', '', '', '', '', '', '', '', '', '', NULL);
